] postNotificationName:kNOTIFICATION_CHANGESTATE object:self userInfo:[NSDictionary dictionaryWithObject:[NSNumber numberWithInt:kGameState_Title] forKey:kNOTIFICATION_CHANGESTATE]];}- (void) pause:(BOOL)flag {	if (flag) {		if (_timer) {			NSLog(@"pause timer");			[_timer invalidate];			_timer = nil;			_pauseTime = CFAbsoluteTimeGetCurrent() - _startTime;			_timerPaused = YES;		}	} else if (_timerPaused) {		NSLog(@"unpause timer");		_startTime = CFAbsoluteTimeGetCurrent();		_timer = [NSTimer scheduledTimerWithTimeInterval:(_pauseTime) target:self selector:@selector(fadeView) userInfo:nil repeats:NO];	}}@end